SELECT "Eixo_1"."nome da sit matricula (situacao detalhada)" AS "nome da sit matricula (situacao detalhada)",   SUM(CAST("Eixo_1"."Number of Records" AS BIGINT)) AS "sum:Number of Records:ok" FROM "Eixo_1" WHERE ((NOT (("Eixo_1"."nome da sit matricula (situacao detalhada)" IN ('AGUARD_CONF', 'CANC_DESISTENTE', 'CANC_MAT_PRIM_OPCAO', 'CANC_SANÇÃO', 'CANC_SEM_FREQ_INICIAL', 'CANC_TURMA', 'DOC_INSUFIC', 'ESCOL_INSUFIC', 'INC _ITINERARIO', 'INSC_CANC', 'NÃO_COMPARECEU', 'TURMA_CANC', 'VAGAS_INSUFIC')) OR ("Eixo_1"."nome da sit matricula (situacao detalhada)" IS NULL))) AND (NOT ("Eixo_1"."situacao_da_turma" IN ('CANCELADA', 'CRIADA', 'PUBLICADA')))) GROUP BY "Eixo_1"."nome da sit matricula (situacao detalhada)";
