SELECT "Generico_5"."Categoria" AS "Categoria" FROM "Generico_5" WHERE (("Generico_5"."Anunciante" IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) >= 2010) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) <= 2015) AND ("Generico_5"."Medio" = 'TELEVISION NACIONAL') AND ((CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) IS NULL) OR ((CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) >= 2) AND (CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) <= 12))) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) = 2015)) GROUP BY "Generico_5"."Categoria";
