SELECT "Generico_5"."Categoria" AS "Categoria" FROM "Generico_5" WHERE (("Generico_5"."Anunciante" IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) >= 2010) AND (CAST(EXTRACT(YEAR FROM "Generico_5"."FECHA") AS BIGINT) <= 2015) AND (((CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) >= 1) AND (CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) <= 7)) OR (CAST(EXTRACT(MONTH FROM "Generico_5"."FECHA") AS BIGINT) IS NULL))) GROUP BY "Generico_5"."Categoria";
