SELECT "MLB_48"."batter_name" AS "batter_name",   (SUM(CAST("MLB_48"."wRC." AS SIGNED)) * -1) AS "usr:Calculation_541206061063524356:ok" FROM "MLB_48" WHERE ((CAST("MLB_48"."PA" AS SIGNED) >= 1) AND (CAST("MLB_48"."PA" AS SIGNED) <= 355) AND ("MLB_48"."homeaway" = 'Away') AND ("MLB_48"."league" = 'PCL') AND (CAST("MLB_48"."year" AS SIGNED) = 2015)) GROUP BY "MLB_48"."batter_name";
