SELECT CAST(EXTRACT(MONTH FROM "Motos_2"."FECHA") AS BIGINT) AS "mn:FECHA:ok" FROM "Motos_2" WHERE (("Motos_2"."Categoria" IN ('CAMIONES', 'CAMIONES, BUSES Y PANELES', 'MOTOCICLETAS', 'PICK UPS, VANS Y JEEPS', 'PICK-UPS', 'SUV Y JEEPS', 'VEHICULOS NUEVOS')) AND (CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) >= 2010) AND (CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) <= 2015) AND (CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) = 2015) AND ("Motos_2"."Categoria" = 'MOTOCICLETAS') AND ("Motos_2"."Medio" = 'RADIO')) GROUP BY "mn:FECHA:ok";
