SELECT "Motos_1"."Medio" AS "Medio" FROM "Motos_1" WHERE (('Cerrar' = 'Cerrar') AND ("Motos_1"."Categoria" IN ('CAMIONES', 'CAMIONES, BUSES Y PANELES', 'MOTOCICLETAS', 'PICK UPS, VANS Y JEEPS', 'PICK-UPS', 'SUV Y JEEPS', 'VEHICULOS NUEVOS')) AND (CAST(EXTRACT(YEAR FROM "Motos_1"."FECHA") AS BIGINT) >= 2010) AND (CAST(EXTRACT(YEAR FROM "Motos_1"."FECHA") AS BIGINT) <= 2015) AND ("Motos_1"."Categoria" = 'MOTOCICLETAS') AND ("Motos_1"."Marca" IN ('MOTOCICLETAS YAMAHA', 'YAMAHA')) AND (CAST(EXTRACT(MONTH FROM "Motos_1"."FECHA") AS BIGINT) >= 1) AND (CAST(EXTRACT(MONTH FROM "Motos_1"."FECHA") AS BIGINT) <= 9)) GROUP BY "Motos_1"."Medio";
