SELECT "Hatred_1"."Keyword" AS "Keyword",   "Hatred_1"."State " AS "State ",   SUM(CAST("Hatred_1"."Number of Records" AS BIGINT)) AS "sum:Number of Records:ok" FROM "Hatred_1" WHERE (("Hatred_1"."Keyword" IN ('camel jockey', 'lesbo', 'man in a dress', 'men in dresses', 'porch monkey')) OR (("Hatred_1"."Keyword" >= 'beaner') AND ("Hatred_1"."Keyword" <= 'bimbo')) OR (("Hatred_1"."Keyword" >= 'chauvinist pig') AND ("Hatred_1"."Keyword" <= 'cripple')) OR (("Hatred_1"."Keyword" >= 'dindu') AND ("Hatred_1"."Keyword" <= 'dyke')) OR (("Hatred_1"."Keyword" >= 'fatass') AND ("Hatred_1"."Keyword" <= 'injun')) OR (("Hatred_1"."Keyword" >= 'japs') AND ("Hatred_1"."Keyword" <= 'kike')) OR (("Hatred_1"."Keyword" >= 'midget') AND ("Hatred_1"."Keyword" <= 'moroccan')) OR (("Hatred_1"."Keyword" >= 'nigger') AND ("Hatred_1"."Keyword" <= 'papist')) OR (("Hatred_1"."Keyword" >= 'rag head') AND ("Hatred_1"."Keyword" <= 'sandnigger')) OR (("Hatred_1"."Keyword" >= 'shemale') AND ("Hatred_1"."Keyword" <= 'trans men')) OR (("Hatred_1"."Keyword" >= 'transsexual') AND ("Hatred_1"."Keyword" <= 'wetback')) OR (("Hatred_1"."Keyword" >= 'whitey') AND ("Hatred_1"."Keyword" <= 'yid'))) GROUP BY "Hatred_1"."Keyword",   "Hatred_1"."State " HAVING ((SUM(1) >= 30) AND (SUM(1) <= 100000));
